import './App.css';
import React from 'react';


function App() {

  return (
    <div>
      <h1>Bank Transactions</h1>
      <input
        type="text"
        placeholder="Search transactions"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select value={sortCriteria} onChange={handleSortChange}>
        <option value="">Sort by</option>
        <option value="category">Category</option>
        <option value="description">Description</option>
      </select>
      <AddTransactionForm onSubmit={addTransaction} />
      <TransactionsTable transactions={sortTransactions(filterTransactions())} />
    </div>
  );

}
function AddTransactionForm({ onSubmit }) {
  const [transaction, setTransaction] = useState({ description: '', amount: '', category: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransaction({ ...transaction, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ ...transaction, id: Date.now() });
    setTransaction({ description: '', amount: '', category: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="description"
        placeholder="Description"
        value={transaction.description}
        onChange={handleChange}
      />
      <input
        type="number"
        name="amount"
        placeholder="Amount"
        value={transaction.amount}
        onChange={handleChange}
      />
      <input
        type="text"
        name="category"
        placeholder="Category"
        value={transaction.category}
        onChange={handleChange}
      />
      <button type="submit">Add Transaction</button>
    </form>
  );
}

function TransactionsTable({ transactions }) {
  return (
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Description</th>
          <th>Amount</th>
          <th>Category</th>
        </tr>
      </thead>
      <tbody>
        {transactions.map(transaction => (
          <tr key={transaction.id}>
            <td>{transaction.date}</td>
            <td>{transaction.description}</td>
            <td>{transaction.amount}</td>
            <td>{transaction.category}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
export default App;
