import './App.css';
import React from 'react';


function App() {
  const [transactions, setTransactions] = useState([
    { id: 1, date: '2024-04-25', description: 'Groceries', amount: -50, category: 'Food' },
    { id: 2, date: '2024-04-24', description: 'Salary', amount: 1000, category: 'Income' },
    // Add more sample transactions here
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [sortCriteria, setSortCriteria] = useState('');

  const addTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);
  };

  const filterTransactions = () => {
    return transactions.filter(transaction =>
      transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const sortTransactions = (filteredTransactions) => {
    if (sortCriteria === 'category') {
      return [...filteredTransactions].sort((a, b) => a.category.localeCompare(b.category));
    } else if (sortCriteria === 'description') {
      return [...filteredTransactions].sort((a, b) => a.description.localeCompare(b.description));
    }
    return filteredTransactions;
  };

  const handleSortChange = (event) => {
    setSortCriteria(event.target.value);
  };

  return (
    <div>
      <h1>Bank Transactions</h1>
      <input
        type="text"
        placeholder="Search transactions"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select value={sortCriteria} onChange={handleSortChange}>
        <option value="">Sort by</option>
        <option value="category">Category</option>
        <option value="description">Description</option>
      </select>
      <AddTransactionForm onSubmit={addTransaction} />
      <TransactionsTable transactions={sortTransactions(filterTransactions())} />
    </div>
  );

}
function AddTransactionForm({ onSubmit }) {
  const [transaction, setTransaction] = useState({ description: '', amount: '', category: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransaction({ ...transaction, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ ...transaction, id: Date.now() });
    setTransaction({ description: '', amount: '', category: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="description"
        placeholder="Description"
        value={transaction.description}
        onChange={handleChange}
      />
      <input
        type="number"
        name="amount"
        placeholder="Amount"
        value={transaction.amount}
        onChange={handleChange}
      />
      <input
        type="text"
        name="category"
        placeholder="Category"
        value={transaction.category}
        onChange={handleChange}
      />
      <button type="submit">Add Transaction</button>
    </form>
  );
}

function TransactionsTable({ transactions }) {
  return (
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Description</th>
          <th>Amount</th>
          <th>Category</th>
        </tr>
      </thead>
      <tbody>
        {transactions.map(transaction => (
          <tr key={transaction.id}>
            <td>{transaction.date}</td>
            <td>{transaction.description}</td>
            <td>{transaction.amount}</td>
            <td>{transaction.category}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
export default App;
